import { SQLite } from 'expo'

const db = SQLite.openDatabase('StickyPeach.db')

export function initDatabase(defaultSettings) {
    db.transaction(tx => {

        // tx.executeSql(
        //     'drop table default_settings;'
        // )
        tx.executeSql(
            'create table if not exists default_settings (id integer primary key not null, name text not null, val_text text, val_int int, val_timestamp timestamp, val_boolean boolean, val_blob blob);'
        )

        // tx.executeSql(
        //     'drop table user;'
        // )
        tx.executeSql(
            'create table if not exists user (id integer primary key not null, name text not null, email text not null, password text not null, timestamp_creation timestamp not null);'
        )

        // tx.executeSql(
        //     'drop table recipe;'
        // )
        tx.executeSql(
            'create table if not exists recipe (id integer primary key not null, name text not null, time_preparation int not null, time_cook int not null, serves int not null, description text not null, vegan boolean not null, picture blob, timestamp_creation timestamp not null, timestamp_updated timestamp, user_id int not null, FOREIGN KEY(user_id) REFERENCES user(id));'
        )

        // tx.executeSql(
        //     'drop table step;'
        // )
        tx.executeSql(
            'create table if not exists step (id integer primary key not null, orderNumber int not null, description text not null, picture blob, recipe_id int not null, timestamp_creation timestamp not null, timestamp_updated timestamp, FOREIGN KEY(recipe_id) REFERENCES recipe(id));'
        )

        // tx.executeSql(
        //     'drop table material;'
        // )
        tx.executeSql(
            'create table if not exists material (id integer primary key not null, orderNumber int not null, description text not null, picture blob, recipe_id int not null, timestamp_creation timestamp not null, timestamp_updated timestamp, FOREIGN KEY(recipe_id) REFERENCES recipe(id));'
        )

        // tx.executeSql(
        //     'drop table ingredient;'
        // )
        tx.executeSql(
            'create table if not exists ingredient (id integer primary key not null, orderNumber int not null, description text not null, picture blob, recipe_id int not null, timestamp_creation timestamp not null, timestamp_updated timestamp, FOREIGN KEY(recipe_id) REFERENCES recipe(id));'
        )

        // tx.executeSql(
        //     'drop table collection;'
        // )
        tx.executeSql(
            'create table if not exists collection (id integer primary key not null, name text not null, description text not null, picture blob, timestamp_creation timestamp not null, timestamp_updated timestamp);'
        )

        // tx.executeSql(
        //     'drop table recipe_collection;'
        // )
        tx.executeSql(
            'create table if not exists recipe_collection (recipe_id int not null, collection_id int not null, FOREIGN KEY(recipe_id) REFERENCES recipe(id), FOREIGN KEY(collection_id) REFERENCES collection(id));'
        )

        // initDBEntries(defaultSettings)
    })

}

function initDBEntries(defaultSettings) {
    initDBUser(defaultSettings)
    initDBDefaultSettings(defaultSettings)
}

function initDBDefaultSettings(defaultSettings) {
    const default_picture = ""
    
    db.transaction(tx => {

        tx.executeSql(
            'drop table default_settings;'
        )
        tx.executeSql(
            'create table if not exists default_settings (id integer primary key not null, name text not null, val_text text, val_int int, val_timestamp timestamp, val_boolean boolean, val_blob blob);'
        )
        tx.executeSql(
            'insert into default_settings (name, val_blob) values (?, ?);'
            , [defaultSettings.PICTURE_NAME, default_picture]
        );
        
    })
}

function initDBUser() {
    
    db.transaction(tx => {

        tx.executeSql(
            'drop table user;'
        )
        tx.executeSql(
            'create table if not exists user (id integer primary key not null, name text not null, email text not null, password text not null, timestamp_creation timestamp not null);'
        )
        tx.executeSql(
            'insert into USER (name, email, password, timestamp_creation) values (?, ?, ?, ?);'
            , ['Filipe Botelho', 'fff@fff@ff', 'qwerty', new Date() ]
        );
        
    })
}